openTest:
it('Variable `age` contains a value', () => {
  expect(age).not.undefined;
});

it('The variable `age` type is a number', () => {
  expect(age).a('number');
});

it('the variable `age` is not NaN', () => {
  expect(isNaN(age)).false;
});

it('the variable `age` is incremented by 3', () => {
  expect(age).equal(16);
});
