function averageScore(exam1, exam2, exam3, exam4, exam5, userName) {
  let sum = 0;
  let count = 0;

  if (exam1.name === userName) {
    sum += exam1.score;
    count++;
  }

  if (exam2.name === userName) {
    sum += exam2.score;
    count++;
  }

  if (exam3.name === userName) {
    sum += exam3.score;
    count++;
  }

  if (exam4.name === userName) {
    sum += exam4.score;
    count++;
  }

  if (exam5.name === userName) {
    sum += exam5.score;
    count++;
  }

  return count ? sum / count : 0;
}

