function addTitleString(names) {
  if (names.length !== 3) {
    return 'Invalid input';
  }
  names[0] = names[0].trim();
  names[1] = names[1].trim();
  names[2] = names[2].trim();
  names.push(`${names[0]} ${names[1]} ${names[2]}`);
  return names;
}

