function isPrime(n){
  if(n < 3) return true
  for(let i = 2; i < n; i++){
    if(n % i === 0) return false
  }
    return true
}

let sum = 0
let arr = []
for(let i = 2; i <= 6; i++){
  if(isPrime(i)){
    arr.push(i)
    sum += i
  }
}
console.log(arr);
```

