function reverseOnlyLastWord(str){
  let result = '';
  let lastWord = '';
  let lastSpace = str.lastIndexOf(' ');
  for(let i = 0; i < str.length; i++){
    if(i > lastSpace) lastWord = str[i] + lastWord;
    else result += str[i];
  }
  return result +lastWord;
}

