const numbers = {
  0: 'zero',
  1: 'one',
  2: 'two'
}; 

function changeNumberToWord(str){
  let result = '';
let word = '';

for(let i = 0; i < str.length; i++){
    if(str[i] !== ' ') word += str[i];
    if(str[i] === ' ' && word) {
        if(numbers[word]) result += numbers[word];
        else result += word;
        word = '';
    }

    if(str[i] === ' ') result += ' ';
  }
  return result;
};

console.log(changeNumberToWord('1 + 2 = 3'));

