    if(str[i] !== ' ') word += str[i];
    if(str[i] === ' ' && word || i === str.length - 1) {
        if(numbers[word]) result += numbers[word];
        else if(!Number.isNaN(+word)) result += '!404!';
        else result += word;
        word = '';
    }
    if(str[i] === ' ') result += ' ';
  }
  return result;
};
console.log(changeNumberToWord('100 - 95 = 5'));
