function findNumbersAndDifference(arr) {
  let lastNumber = 0;
  for (let i = arr.length-1; i >= 0; i--) {
    if (typeof arr[i] === 'number') {
      lastNumber = arr[i]; break
    }
    }
  let difference = lastNumber;
   for (let i = arr.length-1; i >= 0; i--) {
    if (typeof arr[i] === 'number') {
      difference -= arr[i]
    }
    }
   return difference + lastNumber;
}

